public static void lookupCustVendAccount(FormControl _formControl, CustVendACType _custVendACType, SysGroup _custVendGroupId = "")
{
    if( _custVendACType == CustVendACType::Cust )
    {
        Query query = new Query();
        QueryBuildDataSource queryBuildDataSource;

        SysTableLookup sysTableLookup = SysTableLookup::newParameters(tableNum(custTable), _formControl);

        sysTableLookup.addLookupField(fieldNum(CustTable, AccountNum));
        sysTableLookup.addLookupMethod(tableMethodStr(CustTable, name));
        sysTableLookup.addLookupField(fieldNum(CustTable, CustGroup));

        queryBuildDataSource = query.addDataSource(tableNum(CustTable));

        if( _custVendGroupId )
            queryBuildDataSource.addRange(fieldNum(CustTable, CustGroup)).value(_custVendGroupId);

        sysTableLookup.parmQuery(query);

        sysTableLookup.performFormLookup();
    }
    else
    {
        Query query = new Query();
        QueryBuildDataSource queryBuildDataSource;

        SysTableLookup sysTableLookup = SysTableLookup::newParameters(tableNum(VendTable), _formControl);

        sysTableLookup.addLookupField(fieldNum(VendTable, AccountNum));
        sysTableLookup.addLookupMethod(tableMethodStr(VendTable, name));
        sysTableLookup.addLookupField(fieldNum(VendTable, VendGroup));

        queryBuildDataSource = query.addDataSource(tableNum(VendTable));

        sysTableLookup.parmQuery(query);

        sysTableLookup.performFormLookup();
    }
}

