    public static ExchRate getExchRate(CurrencyCode _fromCurrencyCode, 
        TransDate _transDate = systemDateGet(),
		CurrencyCode _toCurrencyCode = Ledger::find(Ledger::current()).AccountingCurrency,
		ExchangeRateTypeId _exchangeRateTypeId = Ledger::find(Ledger::current()).DefaultExchangeRateType)
    {
        ExchangeRate     exchangeRate;
        ExchangeRateType ExchangeRateType;
        ExchangeRateCurrencyPair exchangeRateCurrencyPair;
        real             exchRate;

        select firstonly RecId from exchangeRateCurrencyPair
                where exchangeRateCurrencyPair.ExchangeRateType == _exchangeRateTypeId
                &&  exchangeRateCurrencyPair.FromCurrencyCode == _fromCurrencyCode
                &&  exchangeRateCurrencyPair.ToCurrencyCode   == _toCurrencyCode;

        if( exchangeRateCurrencyPair.RecId )
        {
            exchRate = exchangeRate::findByDate(exchangeRateCurrencyPair.RecId, _transDate).ExchangeRate;
            return exchRate/100;
        }
        else
        {
            return 0;
        }
    }

public static AmountCur curAmount2CurAmount(CurrencyCode _fromCurrencyCode,
    CurrencyCode _toCurrencyCode,
    Amount _amountCur,
    TransDate _transDate = systemDateGet(),
    ExchangeRateTypeId _exchangeRateTypeId = Ledger::find(Ledger::current()).DefaultExchangeRateType)
{
    CurrencyExchangeHelper currencyExchangeHelper;

    currencyExchangeHelper = CurrencyExchangeHelper::newExchangeDate(Ledger::current(), _transDate);
    currencyExchangeHelper.parmExchangeRateTypeRecId(_exchangeRateTypeId);

    return currencyExchangeHelper.calculateCurrencyToCurrencyAmounts(_fromCurrencyCode, _toCurrencyCode, _amountCur).getRoundedAmount();

}
