/// <summary>
/// Gets the temporary file path where a file will be saved.
/// </summary>
/// <returns>
/// A temporary file path.
/// </returns>
/// <remarks>
/// If code is running on the client, the My Documents folder of the user is returned; otherwise, a
/// temporary path for the user running the AOS is returned.
/// </remarks>
public static str getTempFilePath()
{
    #WinAPI

    if (isRunningOnServer() || xSession::isCLRSession())
    {
        new FileIOPermission('', 'r').assert();
        return WinAPIServer::getTempPath();
    }
    else
    {
        return WinAPI::getFolderPath(#CSIDL_PERSONAL);
    }
}

public static str processCLRErrorException(boolean _throw = true )
{
    str strError;
    CLRObject exc;
    CLRObject innerExc;
    CLRObject clrExcMessage;

    exc = CLRInterop::getLastException();
    if(exc)
    {
        clrExcMessage = exc.get_Message();
        innerExc = exc.get_InnerException();
        while(innerExc != null)
        {
            clrExcMessage = innerExc.get_Message();
            innerExc = innerExc.get_InnerException();
        }

        strError = CLRInterop::getAnyTypeForObject(clrExcMessage);

        if (_throw)
        {
            throw error("Update has been cancelled");
        }
    }

    return strError;
}

    public static str compareStringLengthWithFieldMaxLength(TableId _tableId, FieldId _fieldId, str _txt)
    {
        SysDictField dictField = new SysDictField(_tableId, _fieldId);

		// Metin alana girilecek değerin uzunluğu alanın en yüksek uzunluğundan fazla ise sistem hata fırlatıp işlemi kesiyor
		// Bu hatayı almamak için metin kırpılıp gönderilecektir
		if( _txt && dictField && strLen(_txt) > dictField.stringLen() )
		{
			_txt = subStr(_txt, 0, dictField.stringLen() );
		}

        return _txt;
    }

    public server static str decryptReal(int64 _encryptCode, str _oldValue)
    {
        CryptoApi cryptoApi;
        Container cont,cont1;
        ContainerClass cc;


        /* Salt is like a password, While encrypting and descrypting the phrase,
		the CryptoAPI class has to instantiated with same salt(99999999999).
		The phrases/words are encrypted & decrypted based on the salt. */
        cryptoApi = new CryptoApi(_encryptCode);
        cont = BinData::stringToData(_oldValue);
        cont1 = ContainerClass::blob2Container(CryptoApi.decrypt(cont));

        return con2str(cont1);
    }

    public server static str cryptReal(int64 _encryptCode, real _oldValue)
    {
        CryptoApi cryptoApi;
        Container cont,cont1;
        ContainerClass cc;


        /* Salt is like a password, While encrypting and descrypting the phrase,
		the CryptoAPI class has to instantiated with same salt(99999999999).
		The phrases/words are encrypted & decrypted based on the salt. */
        if(_encryptCode != 0)
        {
            cryptoApi = new CryptoApi(_encryptCode);
            cc = new ContainerClass([num2Str(_oldValue, 2,2,1,0)]);
            cont = CryptoApi.encrypt(cc.toBlob()); // The encrypt method requires BLOB as a parameter
            //cont1 = ContainerClass::blob2Container(CryptoApi.decrypt(cont));
            return BinData::dataToString(cont);
        }
        else
			return '0';
        //info(con2str(cont1));
    }

    public server static void testCryptTxt()
    {
        CryptoApi cryptoApi;
        Container cont,cont1;
        ContainerClass cc;


        /* Salt is like a password, While encrypting and descrypting the phrase,
		the CryptoAPI class has to instantiated with same salt(99999999999).
		The phrases/words are encrypted & decrypted based on the salt. */
        cryptoApi = new CryptoApi(99999999999);
        cc = new ContainerClass(["test123"]);
        cont = CryptoApi.encrypt(cc.toBlob()); // The encrypt method requires BLOB as a parameter
        cont1 = ContainerClass::blob2Container(CryptoApi.decrypt(cont));
        info(Strfmt("Encrypted:%1",BinData::dataToString(cont)));
        info(con2str(cont1));
    }

    public static void attachFile(str _filePath, TableId _refTableId, RefRecId _refRecId, DataAreaId _refDataAreaId, boolean _showInBrowser = false)
    {
        System.IO.Stream        stream;

        using (System.IO.FileStream fileStream = new System.IO.FileStream(_filePath, System.IO.FileMode::OpenOrCreate, System.IO.FileAccess::ReadWrite))
        {
            stream = fileStream;
            stream.Seek(0, System.IO.SeekOrigin::Begin);

            DocuRef docuref = DocumentManagement::attachFile(_refTableId, _refRecId, _refDataAreaId,
				DocuType::typeFile(), stream, System.IO.Path::GetFileName(_filePath), System.Web.MimeMapping::GetMimeMapping(_filePath), System.IO.Path::GetFileName(_filePath));

            // download/view the attachment
            if( _showInBrowser )
            {
                str displayUrl = DocumentManagement::getAttachmentPublicUrl(docuref);
                Browser br = new Browser();
                br.navigate(displayUrl);
            }
        }
    }

public static void deleteAttachments(TableId _refTableId, RefRecId _refRecId, DataAreaId _refDataAreaId)
{
    DocuRef docuRef;

    ttsbegin;

    delete_from docuRef
        where docuRef.RefTableId == _refTableId
        && docuRef.RefRecId == _refRecId
        && docuRef.RefCompanyId == _refDataAreaId;

    ttscommit;
}

